sp_widget.client_script:
function($rootScope,$scope, spUtil) {
	/* widget controller */
	var c = this;
	// Use a position indicator to know which record to update
	c.notePos = 0;

	c.selectItem = function(idx) {  
		// Set the position indicator to the index value passed in on select
		c.notePos = idx;
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}
	// Subscribe to event.  Set the new title and note values on the 
	// c.data object
	$rootScope.$on('updateTitle', function(event,data) { 
		c.data.notes[c.notePos].title = data.title;
		c.data.notes[c.notePos].note = data.note;
	});

	c.newNote = function() {
		c.server.get({
			action: 'newNote'
		}).then(function(r) {
			c.data.notes.unshift(r.data.newNote);
			c.data.noteID = r.data.noteID;
			$rootScope.noteID = c.data.noteID;
			$rootScope.$emit('selectNote', c.data.noteID);
		});
	}

	c.updateNote = function(updatedNoteObj) {
		for (var i = 0; i < c.data.notes.length; i++) {
			if (c.data.notes[i].sys_id == updatedNoteObj.sys_id) {
				if (updatedNoteObj.record.title) {
					c.data.notes[i].title = updatedNoteObj.record.title.display_value;
				}
				if (updatedNoteObj.record.note) {
					c.data.notes[i].note = updatedNoteObj.record.note.display_value;
				}
				c.server.update();
				break;
			}
		}
	}

	c.deleteNote = function(sys_id) {
		for (var i = 0; i < c.data.notes.length; i++) {
			if (c.data.notes[i].sys_id == sys_id) {
				if (c.notePos == i) {
					//TODO
					$rootScope.$emit('clearNote');
				}
				console.log('-----------BEFORE----del index-----' + JSON.stringify(c.data.notes));

				c.data.notes.splice(i, 1);
				console.log('-------AFTER--------del index-----' + JSON.stringify(c.data.notes));
				c.server.get({action:'deleteNote', sysId: sys_id}).then(function(r){
					c.data.notes = r.data.notes;
				}) ;
				break;
			}
		}
	}

	$rootScope.$on('deleteNote', function(event,data) {
		console.log('-------------$on--deleteNote-----' + c.notePos);
		//c.data.notes.splice(c.notePos, 1);
		c.deleteNote(data);
	});

	spUtil.recordWatch($scope, "x_snc_createnotes_note", "", function(name) {
		console.log(name.data.operation); 
		console.log(name); 
		if (name.data.operation == 'insert') {
			c.server.refresh();
		} else if (name.data.operation == 'delete') {
		  c.deleteNote(name.data.sys_id);
		} else if (name.data.operation == 'update') {
			c.updateNote(name.data);
		}
	});

}

sp_widget.script:
(function() {

	gs.addInfoMessage('REFRESHING--- SS Notes List');

	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.setLimit(options.max_recs);
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while (noteGR.next()) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		noteObj.note = noteGR.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}

	if (input) {
		if (input.action == 'newNote') {
			var newNote = new GlideRecord('x_snc_createnotes_note');
			newNote.newRecord();
			var id = newNote.insert();
			data.noteID = id;
			data.newNote = {};
			$sp.getRecordValues(data.newNote,newNote,"title,note,sys_id");

		} else if (input.action == 'deleteNote') {
			var deletedNote = new GlideRecord('x_snc_createnotes_note');
			if (deletedNote.get(input.sysId)) {
				deletedNote.deleteRecord();
			}

		}
	}
}
)();
