sp_widget.client_script:
function($rootScope, $scope) {
	/* widget controller */
	var c = this;
	var notePos = -1;

	// c.data.notes - loaded by Server script

	c.selectItem = function(idx) {
		notePos = idx;
		var id = c.data.notes[idx].sys_id;

		//	$rootScope.noteID = id;
		$rootScope.$emit('displayNote', id);
	}

	$rootScope.$on('noteIsUpdated', function(event, noteTitle, noteNote) {
		c.data.notes[notePos].title = noteTitle;
		c.data.notes[notePos].note = noteNote;
	});

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	var noteGR = new GlideRecord('x_snc_createnotes_note'); 
	noteGR.addQuery('user', gs.getUserID());
	noteGR.orderBy('sys_created_on');
	noteGR.query();

	data.notes = [];
	while (noteGR.next()) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number, title, sys_id');
		noteObj.note = noteGR.getValue('note').slice(0, 20);
		data.notes.push(noteObj);
	}

	if (input && input.action == 'reloadNoteById') {
//		data.notes.forEach(element => console.log(element));

	}

})();
