sp_widget.client_script:
function($scope, $rootScope) {
	/* widget controller */
	var c = this;

	// c.data - empty

	c.display = function reload(res) {
		c.data.title = res.data.title;
		c.data.note = res.data.note;
		c.data.sys_id = res.data.sys_id;
	}

	c.updateNote = function updateNote() {

		$rootScope.$emit('noteIsUpdated', c.data);
		c.server.get({action : 'updateNote', 
									args : { noteId : c.data.sys_id,
													noteTitle : c.data.title,
													noteNote : c.data.note
												 }}).then();
	}

	$rootScope.$on('displayNote', function(event, id) { 
		c.server.get({action:'loadNode', args: { noteId : id}}).then(c.display);
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if (input) {

		var nn = new GlideRecord('x_snc_createnotes_note');
		nn.get(input.args.noteId);

		if (input.action == 'loadNode') {

			$sp.getRecordDisplayValues(data, nn, 'title, sys_id, note, user');

		} else if (input.action == 'updateNote') {

			nn.title = input.args.noteTitle;
			nn.note = input.args.noteNote;
			nn.update();
		}
	}

})();
