sp_widget.client_script:
function($scope, $rootScope) {
	/* widget controller */
	var c = this;

	// c.data - empty

	c.updateNote = function updateNote(updateType) {

		c.server.get({action : 'updateNote', 
									noteId : c.data.sys_id,
									noteTitle : c.data.title,
									noteNote : c.data.note
								 }).then(function(r) {
			if (updateType == 'title' || updateType == 'note') {
				$rootScope.$emit('noteIsUpdated', r.data);
			}
		});

	}

	$rootScope.$on('displayNote', function(event, id) { 
		c.server.get({
			action:'loadNode', 
			noteId : id}).then(

			function reload(r) {
				c.data.title = r.data.title;
				c.data.note = r.data.note;
				c.data.sys_id = r.data.sys_id;
			});
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if (input) {

		var noteGR = new GlideRecord('x_snc_createnotes_note');
		noteGR.get(input.noteId);

		if (input.action == 'loadNode') {

			$sp.getRecordDisplayValues(data, noteGR, 'title, sys_id, note, user');

		} else if (input.action == 'updateNote') {
			noteGR.title = input.noteTitle;
			noteGR.note = input.noteNote;
			noteGR.update();

			data.title = noteGR.getValue('title');
			data.note = noteGR.getValue('note').slice(0, 20);
		}
	}

})();
